
public class FloatingPointDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
      double doubleValue = 2.16440330489723961032;
      System.out.println(doubleValue);
      
      float floatValue = 2.16440330489723961032F;
      System.out.println(floatValue);
      
      int b = 15;
      int a = 10;
      
      int quotient = b / a;
      int remainder = b % a;
      
      System.out.println(quotient);
      System.out.println(remainder);
      
      System.out.println("The remainder of 5/5 is " + 5 % 5);
      
      System.out.println( 6 + 7 * 2);
      System.out.println(" 6 + 7 * 2 = " + 6 + 7 * 2);
      
      a = 42;
      a++; 
      //same as a = a + 1, same as a += 1
      
      System.out.println(a);
      
	}

}
 