
public class SmallestEvenNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
// three consecutive even numbers add up to 48
		
		int oneThird = 48/3;
		
//consecutive number
		
		int smallest = oneThird - 2;
		int middle = oneThird;
		int largest = oneThird + 2;
		
		System.out.print("The numbers are " + smallest);
		System.out.print(", " + middle +", " + largest);
		
	     
	}

}
