
public class SmallestOfNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
// Sum of 3 consecutive numbers is 72
		
		int oneThird = 72/3;
		
// consecutive numbers
		
		int smallest = oneThird - 1;
		int middle = oneThird;
		int largest = oneThird + 1;
	
        System.out.print("The numbers are " + smallest + ", ");
        System.out.print(middle + ", " + largest);
		
	}

}
